    //初始化数据字典
    function InitDictItem() {
        $(""#CategoryId"").bindSelect({
            url: ""/Job/Category/FindTreeSelectJson"",
        });
        // BindSelect2(""CategoryId"", ""/Job/Category/FindTreeSelectJson"");
        $(""#slProvince"").bindSelect({
            url: ""/Security/Area/GetAllProvince"",
        });
        $('#slProvince').change(function () {
            $('#slCity').empty();
            $(""#slCity"").bindSelect({
                url: ""/Security/Area/GetListCityByProvinceId?id=""+$('#slProvince').val(),
            });
            $('#slCity').change()
        });
        $('#slCity').change(function () {
            $('#slDistrict').empty();
            $(""#slDistrict"").bindSelect({
                url: ""/Security/Area/GetListDistrictByCityId?id=""+$('#slCity').val(),
            });
        });
        $('#slDistrict').change(function () { $(""#RegionId"").val($(this).val()); })
